hudson.tasks.Shell.command:
#Install dependencies
sudo apt-get update
sudo apt-get install -y git wget tar

#Set Environmental Variables
export JAVA_HOME=/opt/ibm/java-s390x-80
export PATH=$JAVA_HOME/bin:$PATH
java -version

#Get the source code
sudo rm -rf mongo-java-driver
git clone https://github.com/mongodb/mongo-java-driver.git
cd mongo-java-driver
git checkout r3.6.1

#Build the Java MongoDb driver
./gradlew assemble

#Install MongoDB server
sudo apt-get install -y wget
wget https://repo.mongodb.com/apt/ubuntu/dists/xenial/mongodb-enterprise/3.5/multiverse/binary-s390x/mongodb-enterprise-unstable-server_3.5.3_s390x.deb
sudo dpkg -i --force-all mongodb-enterprise-unstable-server_3.5.3_s390x.deb 
sudo apt-get install -y -f
sudo mkdir -p db
sudo /usr/bin/mongod --dbpath db &
sleep 40s

#testcases passes on host
#./gradlew check -i -s -Dorg.mongodb.test.uri=mongodb://localhost:27017

echo 'import java.util.ArrayList;
import java.util.List;
import org.bson.Document;
import com.mongodb.MongoClient;
import com.mongodb.client.MongoCollection;
import com.mongodb.client.MongoCursor;
import com.mongodb.client.MongoDatabase;


public class test {
    public static void main(String[] args) {

        String server_name="localhost";
        String database_name="ibm_test_db";
        String collection_name="mongodb_java_driver";

        MongoClient mongoClient = new MongoClient(server_name);
        MongoDatabase database = mongoClient.getDatabase(database_name);
        MongoCollection<Document> collection = database.getCollection(collection_name);

        collection.drop();

        Document doc = new Document("company", "IBM")
            .append("MongoDB Driver", new Document("language", "Java").append("version", "r3.6.1"));
        collection.insertOne(doc);

        List<Document> documents = new ArrayList<Document>();
        for (int i = 0; i < 3; i++) {
            documents.add(new Document("line", i));
        }
        collection.insertMany(documents);

        Document myDoc = collection.find().first();
        System.out.println(myDoc.toJson());
        MongoCursor<Document> cursor = collection.find().iterator();
        try {
            cursor.next();
            while (cursor.hasNext()) {
                System.out.println(cursor.next().toJson());
            }
        } finally {
            cursor.close();
        }
    }
}' > test.java



javac -cp mongo-java-driver/build/libs/mongo-java-driver-3.6.1.jar test.java
java -cp mongo-java-driver/build/libs/mongo-java-driver-3.6.1.jar:. test

